'use strict';
var exec = require('child_process').exec;

var Module = {
	flash : function(firmware, port, callBack) {
		var cmd = 'avr.exe -p m328p -P\\\\.\\' +
			port +
			' -b115200 -Uflash:w:\"' +
			firmware +
			'.hex\":i -C./avrdude.conf -carduino -D';
		exec(
			cmd,
			{
				cwd: './node_modules/flasher'
			},
			callBack
		);
	}
};

module.exports = Module;